        </div>

        <div className="restaurant-menu">
            {/* <img alt="" src="" /> menu images to go here */}
        </div>

        <div className="restaurant-images">
            {/* <img alt="" src="" /> restaurant images here */}
        </div>
        </>
    )

    }
}

export default Restaurant
