import React from "react"

class Search extends React.Component{
    state={
        searchTerm: ""
    }


  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value})

  }

  searchHandler = (e) => {
    e.preventDefault()
    fetch("https://developers.zomato.com/api/v2.1/search?q=&count=20&lat=40.705138&lon=-74.014096&radius=1000&sort=real_distance&order=asc", {
            headers: {
            Accept: "application/json",
            "User-Key": "7dc855cf4405df1034f62de35de0744e"
        }
        })
        .then(resp => resp.json())
        .then(restaurantData => {
            this.setState(() => ({
                restaurants: restaurantData.restaurants
            }))
        })


    this.setState({
      searchTerm: ""
    })
  }



    render(){
        return(
            <>
            <form className="search" onSubmit={this.searchHandler}>
                <input type= "text" name="search" placeholder="Search cuisines, specialties, etc." value={this.state.search} onChange={this.changeHandler}/>
                <input type="submit" value="Search" />
            </form>
            </>
        )
    }


}

export default Search
