import React from "react"
import DateTimePicker from 'react-datetime-picker'


class RestaurantCard extends React.Component{

  state = {
    date: "2020-11-13",
    time: "19:00",
    guests: "2",
    user: "1"
  }

  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value})
  }

  submitHandler = (e) => {

    e.preventDefault()
    let backendRestId = ""
    const restaurant = this.props.restaurant.restaurant
    let restaurantData = {
      name: restaurant.name,
      address: restaurant.location.address,
      zomato_id: restaurant.id

      }
    fetch('http://localhost:3000/api/v1/restaurants', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json'
      },
      body: JSON.stringify(
        restaurantData
      )
    })
    .then(r => r.json())
    .then((r) => {
      backendRestId = r.id;
    })

    .catch(error => console.error(error))


    let reservationData = {
      restaurant_id: backendRestId,
      user_id: this.state.user_id,
      guests: this.state.guests

      }

    fetch('http://localhost:3000/api/v1/reservations', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json'
      },
      body: JSON.stringify(
        reservationData
      )
    })
    .then(r => r.json())
    .then((r) => {
      console.log(r);
    })

    .catch(error => console.error(error))



  }




  render() {

    const restaurant = this.props.restaurant.restaurant


    return(
        <>
                <div key={restaurant.id}>
                    <img src={restaurant.photo_url} alt={restaurant.name}/>
                    <h2>{restaurant.name}</h2>
                    <h4>{restaurant.cuisines}</h4>
                    <p>{restaurant.location.address}</p>

                    <form className="reservation" onSubmit={this.submitHandler}>
                    <label htmlFor="reservation_date">Reservation Date:</label>
                    <DateTimePicker />
                    <label>
                    Guests:
                    <select name="guests" value={this.state.guests} onChange={this.changeHandler}>
                        <option value="1">1</option>
                        <option value="2">2</option>
                        <option value="3">3</option>
                        <option value="4">4</option>
                        <option value="5">5</option>
                        <option value="6">6</option>
                    </select>
                    </label>
                    <input type="submit" value="Make Reservation" />
                    </form>
                    <br/>
                    <br/>

                </div>
              </>
            )}




}






export default RestaurantCard
