import React from "react"

class Search extends React.Component{
    state = {
        searchTerm: "",
        searchedRestaurants: ""
    }


    componentDidMount() {
        changeHandler = (e) => {
            this.setState({ [e.target.name]: e.target.value})

          }

          searchHandler = (e) => {
            console.log(e)
            e.preventDefault()
            let searchURL = 'https://developers.zomato.com/api/v2.1/search?q=&count=20&lat=40.705138&lon=-74.014096&radius=1000&sort=real_distance&order=asc';
            fetch(searchURL, {
                    headers: {
                    Accept: "application/json",
                    "User-Key": "7dc855cf4405df1034f62de35de0744e"
                }
                })
                .then(resp => resp.json())
                .then(restaurantData => {
                    this.setState(() => ({
                        searchedRestaurants: restaurantData.restaurants
                    }))
                })


            this.setState({
              searchTerm: ""
            })
          }

    }


    render(){
        return(
            <>
            <form className="search" onSubmit={this.searchHandler}>
                <input type= "text" name="search" placeholder="Search cuisines, specialties, etc." value={this.state.searchTerm} onChange={this.changeHandler}/>
                <input type="submit" value="Search" />
            </form>
            </>
        )
    }


}

export default Search
